import XCTest
@testable import SwiftGit2
import Essentials
import EssentialsTesting

final class KnownHostsTests: XCTestCase {
    func test_readKnownHosts() {
        let results1 = KnownHosts.getFromText(test1).maybeSuccess!
        
        XCTAssertEqual(results1[0].hosts, ["gitlab.com","35.231.145.151"])
        XCTAssertEqual(results1[0].sshType, "ecdsa-sha2-nistp256")
        XCTAssertEqual(results1[0].key, "AAAAE2VjZHNhLXNoYTItbmlzdHAyNTYAAAAIbmlzdHAyNTYAAABBBFSMqzJeV9rUzU4kWitGjeR4PWSa29SPqJ1fVkhtj3Hw9xjLVXVYrU9QlYWrOLXBpQ6KWjbjTDTdDkoohFzgbEY=")
        
        XCTAssertEqual(results1[1].hosts, ["140.82.118.3"])
        XCTAssertEqual(results1[1].sshType, "ssh-rsa")
        XCTAssertEqual(results1[1].key, "")
        
        XCTAssertEqual(results1.count, 11)
        
        ///////////////////////////////////////////////////////////////////////////////////////
        ///////////////////////////////////////////////////////////////////////////////////////
        let results2 = KnownHosts.getFromText(test2).maybeSuccess!
        
        XCTAssertEqual(results2[0].hosts, ["gitlab.com","35.231.145.151"])
        XCTAssertEqual(results2[0].sshType, "ecdsa-sha2-nistp256")
        XCTAssertEqual(results2[0].key, "AAAAE2VjZHNhLXNoYTItbmlzdHAyNTYAAAAIbmlzdHAyNTYAAABBBFSMqzJeV9rUzU4kWitGjeR4PWSa29SPqJ1fVkhtj3Hw9xjLVXVYrU9QlYWrOLXBpQ6KWjbjTDTdDkoohFzgbEY=")
        
        XCTAssertEqual(results2[1].hosts, ["140.82.118.3"])
        XCTAssertEqual(results2[1].sshType, "ssh-rsa")
        XCTAssertEqual(results2[1].key, "")
        
        XCTAssertEqual(results2[4].hosts, ["140.82.113.3"])
        XCTAssertEqual(results2[4].sshType, "ssh-rsa")
        XCTAssertEqual(results2[4].key, "")
        
        XCTAssertEqual(results2[5].hosts, ["140.82.113.4"])
        XCTAssertEqual(results2[5].sshType, "ssh-rsa")
        XCTAssertEqual(results2[5].key, "")
        
        XCTAssertEqual(results2.count, 6)
        
        ///////////////////////////////////////////////////////////////////////////////////////
        ///////////////////////////////////////////////////////////////////////////////////////
        
        let results3 = KnownHosts.getFromText("").maybeSuccess!
        
        XCTAssertEqual(results3.count, 0)
    }
}

